import { ButtonToggle, CardText, CardHeader, CardFooter } from "reactstrap";
import React, { useState, useEffect } from "react";
//import { Link } from "react-router-dom";

const Apunte = (props) => {
  //const [alumno, setAlumno] = useState("");
  const [titulo, setTitulo] = useState("");
  const [texto, setTexto] = useState("");
  const [fecha, setFecha] = useState("");
  const [etiquetas, setEtiquetas] = useState([]);
  const [likes, setLikes] = useState("");
  const [dislikes, setDislikes] = useState("");

  function Darlike(pid, nlikes) {
    if (!datauData.likes.includes(pid)) {
      //agregar a favoritos
      const likesRef = collectionposts.doc(pid);
      const userdalike = arrayvalue.arrayUnion(user.email);
      likesRef.update({
        likes: Number(nlikes) + 1,
        likedpor: userdalike,
      });
      userDatos.update({
        likes: arrayvalue.arrayUnion(pid),
      });
    } else {
      //quitar de favoritos
      const likesRef = collectionposts.doc(pid);
      const userquitalike = arrayvalue.arrayRemove(user.email);
      likesRef.update({
        likes: Number(nlikes) - 1,
        likedpor: userquitalike,
      });
      userDatos.update({
        likes: arrayvalue.arrayRemove(pid),
      });
    }
  }

  function Dardislike(pid, ndislikes) {
    if (!datauData.dislikes.includes(pid)) {
      //agregar a dislike
      const likesRef = collectionposts.doc(pid);
      likesRef.update({
        dislikes: Number(ndislikes) + 1,
      });
      userDatos.update({
        dislikes: arrayvalue.arrayUnion(pid),
      });
    } else {
      //quitar de dislkike
      const likesRef = collectionposts.doc(pid);
      likesRef.update({
        dislikes: Number(ndislikes) - 1,
      });
      userDatos.update({
        dislikes: arrayvalue.arrayRemove(pid),
      });
    }
  }

  return (
    <div>
      <Card>
        <CardHeader>Post por: {post.autor}</CardHeader>
        <CardBody>
          <CardTitle tag="h5">{post.titulo}</CardTitle>
          <CardText>{post.texto}</CardText>
        </CardBody>
        <CardFooter>
          <ButtonToggle
            color="primary"
            onClick={() => Darlike(post.NO_ID_FIELD, post.likes)}
          >
            Likes: {"" + post.likes}
          </ButtonToggle>{" "}
          <ButtonToggle
            color="primary"
            onClick={() => Dardislike(post.NO_ID_FIELD, post.dislikes)}
          >
            Dislike: {"" + post.dislikes}
          </ButtonToggle>{" "}
          <ButtonToggle
            color="primary"
            onClick={() => borrar(post.NO_ID_FIELD)}
          >
            Compartir
          </ButtonToggle>
        </CardFooter>
      </Card>
    </div>
  );
};

export default Apunte;
