import React from "react";
import {
  useFirestore,
  useFirestoreCollectionData,
  useFirestoreDocData,
} from "reactfire";
import {
  Container,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  Card,
  CardTitle,
  CardBody,
  ButtonGroup,
} from "reactstrap";
import Apunte from "../Components/Apunte";

function Dashboard({ user }) {
  const [titulo, setTitulo] = React.useState("");
  const [texto, setTexto] = React.useState("");
  const [tags, setTags] = React.useState([]);
  const [postsrender, setPostsrender] = React.useState(0);
  const collectionposts = useFirestore().collection("Posts");
  const arrayvalue = useFirestore.FieldValue;
  const userDatos = useFirestore().collection("UserData").doc(user.email);
  const { status: statusPosts, data: dataPosts } =
    useFirestoreCollectionData(collectionposts);
  const { data: datauData } = useFirestoreDocData(userDatos);

  function publicar() {
    const hoy = new Date();
    if (titulo !== "" || texto !== "") {
      collectionposts.add({
        titulo: titulo,
        texto: texto,
        fecha: hoy.toDateString(),
        etiquetas: tags,
        likes: Number(0),
        dislikes: Number(0),
        likedpor: [],
        dislikedpor: [],
      });
      setTitulo("");
      setTexto("");
      tags.splice(0, tags.length);
    }
  }

  return (
    <Container>
      <Form>
        <div>
          <Card>
            <CardBody>
              <CardTitle tag="h3">Sube tu post!</CardTitle>
              <Input
                name="titulo"
                placeholder="Titulo"
                value={titulo}
                onChange={(e) => setTitulo(e.target.value)}
              />
              <br />
              <Input
                type="textarea"
                name="texto"
                placeholder="Post"
                value={texto}
                onChange={(e) => setTexto(e.target.value)}
              />
              <br />
              <Form inline>
                <FormGroup className="mb-2 mr-sm-2 mb-sm-0">
                  <Label for="examplePassword" className="mr-sm-2">
                    Etiquetas:{" "}
                  </Label>
                  <Input type="text" name="tags" id="exampleTags" />
                </FormGroup>
                <Button
                  onClick={
                    ((e) => tags.push(e.target.value),
                    (e.target.value = ""),
                    console.log(tags))
                  }
                >
                  {" "}
                  Agregar{" "}
                </Button>
                <ul>
                  {tags.map((element, index) => (
                    <li key={index}>{element}</li>
                  ))}
                </ul>
              </Form>
              <br />
              <Button size="sm" onClick={() => publicar()}>
                Subir
              </Button>
            </CardBody>
          </Card>
        </div>

        <div>
          <ButtonGroup vertical>
            <FormGroup tag="fieldset">
              <FormGroup check>
                <Label check>
                  <Input
                    type="radio"
                    name="radio1"
                    onChange={() => setPostsrender(0)}
                    defaultChecked
                  />
                  Mostrar todos
                </Label>
              </FormGroup>
              <FormGroup check>
                <Label check>
                  <Input
                    type="radio"
                    name="radio1"
                    onChange={() => setPostsrender(1)}
                  />
                  Mostrar solo post de amigos
                </Label>
              </FormGroup>
            </FormGroup>
          </ButtonGroup>
        </div>

        <div>
          {postsrender === 0
            ? //Todos los post
              statusPosts === "success" && (
                <div>
                  {dataPosts.map((post) => (
                    <div key={post.NO_ID_FIELD}>
                      <Apunte
                        titulo={post.titulo}
                        texto={post.texto}
                        fecha={post.fecha}
                        etiquetas={post.etiquetas}
                        likess={post.likes}
                        dislikes={post.dislikes}
                        likedpor={post.likedpor}
                        dislikedpor={post.dislikes}
                        email={user.email}
                      />
                    </div>
                  ))}
                </div>
              )
            : //Post amigos
              statusPosts === "success" && (
                <div>
                  {dataPosts.map((post) => (
                    //{post.etiquetas.some(x => x.includes(y))
                    // ?
                    <div key={post.NO_ID_FIELD}>
                      <Apunte
                        titulo={post.titulo}
                        texto={post.texto}
                        fecha={post.fecha}
                        etiquetas={post.etiquetas}
                        likess={post.likes}
                        dislikes={post.dislikes}
                        likedpor={post.likedpor}
                        dislikedpor={post.dislikes}
                        email={user.email}
                      />
                    </div>
                    //: null}
                  ))}
                </div>
              )}
        </div>
      </Form>
    </Container>
  );
}

export default Dashboard;
